<?phpnamespace controllers;use models\model;use models\Users;//// Конттроллер страницы чтения.//class C_Page extends C_Base{    public function action_index()    {        $this->title = 'Пользователи';        $model = new Users();        $city = $model->getCities();        $users = $model->getUsers();        $this->content = $this->Template('users', ['city' => $city, 'users' => $users]);    }}