<?phpnamespace controllers;use models\model;use models\Users;//// Конттроллер страницы чтения.////include_once('models/model.php');class C_Page extends C_Base{    // Конструктор.    //    public function action_index()    {        $model = new model();        $this->title .= '::Чтение';        $text = $model->text_get();        $this->content = $this->Template('v_index', ['text' => $text]);    }    public function action_edit()    {        $this->title .= '::Редактирование';        if ($this->isPost()) {            $model = new model();            $model->text_set($_POST['text']);            header('location: index.php');            exit();        }        $model = new model();        $text = $model->text_get();        $this->content = $this->Template('v_edit', ['text' => $text]);    }    public function action_users()    {        $this->title .= '::Пользователи';        $model = new Users();        $city = $model->getCities();        $users = $model->getUsers();        if (!empty($_POST)) {            $vars = $model->addUser();        }        $this->content = $this->Template('users', ['text' => $vars, 'city' => $city, 'users' => $users]);    }}